# coding=utf-8import os,sysimport shutilimport commonimport configimport bucfg# 处理沙盘分类图标def process():	# 先获取sand360的路径	paths = os.listdir(bucfg.prdSand360) # src/product/sand360 目录	for filepath in paths:		if filepath.find(".svn")>-1:			continue		listpath = os.listdir(bucfg.prdSand360+filepath+"\\")		for iconfile in listpath:			if iconfile.find(".svn")>-1:				continue			if iconfile[-4:]=='.png':				newFile = "%sui\\data\\"%(bucfg.targetDir)+iconfile				srcFile = bucfg.prdSand360+filepath+"\\"+iconfile				shutil.copyfile(srcFile,newFile)