# coding=utf-8import os,sysimport shutilimport commonimport configimport bucfg# 处理进度条背景图片、快速定位背景图、校园logo图片、地理信息图片def process():		# 前面已经检查过图片文件，这里就不需要检查了		# 加载页面背景图	srcFile = bucfg.prdUiLoaderImg	newFile = bucfg.targetDir+"ui\\data\\" + bucfg.loadImgName	shutil.copyfile(srcFile, newFile)	# 快速定位背景图	newFile = bucfg.targetDir+"ui\\data\\" + bucfg.prdUiPositionImgName	shutil.copyfile(bucfg.prdUiPositionImg, newFile)		# logo图片	newFile = bucfg.targetDir+"ui\\data\\" + bucfg.prdUiLogoImgName	shutil.copyfile(bucfg.prdUiLogoImg, newFile)		# 地理信息图片	newFile = bucfg.targetDir+"ui\\data\\" + bucfg.prdUiAddrImgName	shutil.copyfile(bucfg.prdUiAddrImg, newFile)	srcPath = "%s\\src"%(config.PROJECT_HOME)	tarPath = "%s\\target"%(config.PROJECT_HOME)		# 沙盘的初始视角	shutil.copyfile(srcPath+"\\product\\position\\sand_view.xml", tarPath+"\\json\\sand_view.xml")	# 快速定位的数据处理	shutil.copyfile(srcPath+"\\product\\position\\quick_position.xml", tarPath+"\\json\\quick_position.xml")